global {
    init {
        float initialBudgetAllocation <- 1000000;
        int timesteps <- 52; // Number of weeks in a year
        float urbanDensity <- 1.5; // Initial urban density (persons per acre)
        float greenSpaceCoverage <- 0.3; // Initial green space coverage (acre)
    }

