experiment {
  // Experiment parameters
  float minimum_cycle_duration <- 0.7;
  float seed <- 1.0;

  parameter diseaseSpreadRate var:diseaseSpreadRate category:Low, Medium, High;
  parameter interventionEffectiveness var:interventionEffectiveness category:Low, Medium, High;

  // Outputs and displays
  output {
    display HotspotEvolution refresh:every(5#cycles) {
      grid gridName;
      species Human aspect: base;
    }
    display CommunityEngagement refresh:every(10#cycles) {
      chart "Community Engagement" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Number of Engaged Community Members" value: ;
      }
    }
    display UrbanDesignImpact refresh:every(15#cycles) {
      chart "Urban Design Impact" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Urban Design Impact Score" value: ;
      }
    }
    display InterventionEffectiveness refresh:every(20#cycles) {
      chart "Intervention Effectiveness" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Intervention Effectiveness Score" value: ;
      }
    }
    display MobilityPatternOptimization refresh:every(25#cycles) {
      chart "Mobility Pattern Optimization" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Mobility Pattern Optimization Score" value: ;
      }
    }
    display ToolValidation refresh:every(30#cycles) {
      chart "Tool Validation" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Tool Validation Score" value: ;
      }
    }
    display TechnologyIntegration refresh:every(35#cycles) {
      chart "Technology Integration" type: histogram/pie/series/scatter/xy/radar/heatmap/box_whisker {
        data "Technology Integration Score" value: ;
      }
    }
  }
}
