        float riskSum <- 0.0;
        int count <- 0;
        foreach (AgriculturalProducer ap in AgriculturalProducer.all) {
            float risk <- 0.0;
            if (ap.pestControlIntensity = "High") risk <- risk + 0.3;
            if (ap.cropYield < 5) risk <- risk + 0.5;
            riskSum <- riskSum + risk;
            count <- count + 1;
