			predator_network_centralization <- 0.0;
		}

		// Calculate Predator Gini Coefficient (for Hypothesis 1)
		if (length(predator_agent) > 1) {
			list<float> hunt_successes <- predator_agent collect each.hunts_succeeded;
			float n <- length(hunt_successes);
			float sum_of_abs_diff <- 0.0;
			loop i from: 0 to: n - 1 {
				loop j from: 0 to: n - 1 {
					sum_of_abs_diff <- sum_of_abs_diff + abs(hunt_successes[i] - hunt_successes[j]);
				}
			}
			float mean_success <- mean(hunt_successes);
			if (mean_success > 0) {
				predator_gini_coefficient <- sum_of_abs_diff / (2 * n * n * mean_success);
			} else {
				predator_gini_coefficient <- 0.0;
			}
		} else {
			predator_gini_coefficient <- 0.0;
		}
	}

	// -----------------------------------------------------------------
	// ---- Model Initialization
	// -----------------------------------------------------------------
	init {
		create prey_agent number: 150 {
			location <- rnd_location_in(shape);
			meme_predation_energy_cost <- predation_energy_cost_factor;
		}
		create predator_agent number: 30 {
			location <- rnd_location_in(shape);
			inoculation_level <- agent_inoculation_level;
		}
	}
}

// -----------------------------------------------------------------
// ---- Species Definitions
// -----------------------------------------------------------------

species agent skills:[moving] {
	float energy_level <- 100.0;
	float speed <- 1.0;
	float reproduction_threshold <- 150.0;
	float energy_cost_of_movement <- 0.1;

	reflex move_and_consume_energy {
		do wander speed: speed;
		energy_level <- energy_level - energy_cost_of_movement;
	}

	reflex check_death {
		if (energy_level <= 0) {
			do die;
		}
	}
}

species prey_agent parent: agent {
	string meme_content <- "Default Meme";
	float meme_predation_energy_cost <- 20.0;
	float meme_source_credibility_score <- 0.5;
	bool is_infected <- true; 
	float propagation_radius <- 5.0;

	reflex check_reproduction {
		if (energy_level > reproduction_threshold) {
			do reproduce;
		}
	}

	action reproduce {
		energy_level <- energy_level / 2.0;
		ask myself {
			create prey_agent number: 1 with:[location::location, energy_level::self.energy_level, parent::self];
		}
	}

	action become_cured {
		is_infected <- false;
	}

	action become_infected {
		is_infected <- true;
	}

	reflex consume_attention {
		energy_level <- energy_level + 2.0;
	}

	reflex propagate {
		if (is_infected) {
			float base_probability <- 0.1;
			if (global.algorithmic_amplification = "Engagement-Biased") {
				base_probability <- base_probability * (1 + (length(prey_agent at_distance 2.0) / 10.0));
			} else if (global.algorithmic_amplification = "Credibility-Biased") {
				base_probability <- base_probability * (1 + meme_source_credibility_score);
			}

			list<prey_agent> targets <- prey_agent at_distance propagation_radius where (!each.is_infected);
			ask targets {
				if (flip(base_probability)) {
					do become_infected;
				}
			}
		}
	}

	aspect default {
		draw circle(1) color: is_infected ? #orange : #green;
	}
}

species predator_agent parent: agent {
	string counter_narrative <- "Default Counter-Narrative";
	string inoculation_level <- "None"; 
	map<string, float> trust_portfolio <- map[]; 
	float hunt_range <- 10.0;
	float hunts_succeeded <- 0.0;

	reflex check_reproduction {
		if (energy_level > reproduction_threshold) {
			do reproduce;
		}
	}

	action reproduce {
		energy_level <- energy_level / 2.0;
		ask myself {
			create predator_agent number: 1 with:[location::location, energy_level::self.energy_level, parent::self];
		}
	}

	action user_kill {
		do die;
	}

	action update_trust from:(predator_agent) with_source:(predator_agent) {
		string source_id <- string(with_source);
		if (trust_portfolio contains_key source_id) {
			trust_portfolio(source_id) <- trust_portfolio(source_id) + 0.05;
		} else {
			trust_portfolio <+ (source_id::0.5);
		}
	}

	reflex hunt {
		energy_level <- energy_level - 5.0; 
		list<prey_agent> targets <- prey_agent at_distance hunt_range where (each.is_infected);

		if (length(targets) > 0) {
			prey_agent my_target <- first(targets with_min_of(each distance_to self));

			float base_success_chance <- 0.8;
			float inoculation_bonus <- 0.0;
			if (inoculation_level = "Basic") { inoculation_bonus <- 0.1; }
			if (inoculation_level = "Advanced") { inoculation_bonus <- 0.25; }
			float success_probability <- base_success_chance + inoculation_bonus - (my_target.meme_predation_energy_cost / 100.0);

			if (flip(success_probability)) {
				energy_level <- energy_level + my_target.meme_predation_energy_cost;
				hunts_succeeded <- hunts_succeeded + 1.0;
				ask my_target {
					do become_cured;
				}
			}
		}
	}

	reflex share_counter_narrative {
		float share_radius <- 8.0;
		list<predator_agent> nearby_predators <- predator_agent at_distance share_radius where (each != self);
		ask nearby_predators {
			do update_trust from: myself with_source: myself;
		}
	}

	aspect default {
		draw triangle(1.5) color: #blue;
		// This user command allows for direct testing of Hypothesis 2 (Systemic Brittleness)
		user_command "Neutralize Predator" action: user_kill;
	}
}

experiment Noosphere_Simulation type: gui {
	// -----------------------------------------------------------------
	// ---- Parameters to manipulate Experimental Factors
	// -----------------------------------------------------------------
	parameter "Meme Difficulty" var: predation_energy_cost_factor min: 0.0 max: 100.0 category: "Experimental Factors";
	parameter "Predator Inoculation" var: agent_inoculation_level among: ["None", "Basic", "Advanced"] category: "Experimental Factors";
	parameter "Platform Algorithm" var: algorithmic_amplification among: ["Neutral", "Engagement-Biased", "Credibility-Biased"] category: "Experimental Factors";

	output {
		// -----------------------------------------------------------------
		// ---- Output 1 & 3: Main Display (Memetic Propagation Visualizer)
		// -----------------------------------------------------------------
		display Simulation_World {
			species prey_agent aspect: default;
			species predator_agent aspect: default;
		}

		// -----------------------------------------------------------------
		// ---- Key Performance Indicators & Monitors
		// -----------------------------------------------------------------
		display Performance_Monitors {
			monitor "Cycle" value: cycle;
			monitor "Memetic Infection Rate (Obj. 2)" value: memetic_infection_rate format: "#0.00%";
			monitor "Quality of Trust Investment (Obj. 2)" value: qti_score format: "#0.000";
			monitor "Predator Network Centralization (Hypo. 1)" value: predator_network_centralization format: "#0.000";
			monitor "Predator Gini Coefficient (Hypo. 1)" value: predator_gini_coefficient format: "#0.000";
		}

		// -----------------------------------------------------------------
		// ---- Time Series Charts for Objectives and Hypotheses
		// -----------------------------------------------------------------
		display Key_Metrics_Over_Time refresh: every(5 #cycles) {
			chart "Objective & Hypothesis Metrics" type: series {
				data "Infection Rate" value: memetic_infection_rate color: #orange;
				data "QTI Score" value: qti_score color: #purple;
				data "Network Centralization" value: predator_network_centralization color: #blue;
			}
		}

		// -----------------------------------------------------------------
		// ---- Output 1: Time-Series Metrics Log (as a file)
		// -----------------------------------------------------------------
		save [cycle, memetic_infection_rate, qti_score, predator_network_centralization, predator_gini_coefficient] to: "time_series_log.csv" type: "csv" header: "Cycle,InfectionRate,QTIScore,Centralization,GiniCoefficient" rewrite: true every: 1 #cycles;
	}
}
