model XylosEcosystemSimulation

global {
	float instabilityLevel <- 0.5;
	int harvestingEfficiency <- 75;

	init {
		create ResourceManager number: 1;
		create Shadowstalker number: 100;
		create DataCollector number: 1;
		create LumifloraPopulation number: 500;
	}
}

species ResourceManager {
	float instabilityLevel <- 0.5;
	int harvestingEfficiency <- 75;

	reflex monitorLumifloraDensity {
		if (LumifloraPopulation.resourceAvailability < 0.3) {
			instabilityLevel <- 1.0;
		} else {
			instabilityLevel <- 0.5;
		}
		DataCollector.provideDataAnalysisReport(instabilityLevel);
	}

	action adjustHarvestingSchedule(int schedule) {
		harvestingEfficiency <- schedule;
		Shadowstalker.respondToAlert(instabilityLevel);
	}

	action alertShadowstalkersOfResourceScarcity() {
		Shadowstalker.respondToAlert(instabilityLevel);
	}
}

species Shadowstalker {
	float tirednessLevel <- 0.2;
	int territoryConflictLevel <- 5;
	float predatorProximity <- 100;

	reflex respondToAlert(float instabilityLevel) {
		if (instabilityLevel > 0.8) {
			tirednessLevel <- tirednessLevel + 0.2;
		}
	}

	action forageForLumiflora() {
		if (LumifloraPopulation.resourceAvailability > 0.3) {
			tirednessLevel <- tirednessLevel - 0.1;
			LumifloraPopulation.consumeResources(1);
		}
	}
}

species DataCollector {
	float dataAccuracyScore <- 0.9;
	int alertFrequency <- 1;
	float sensorMalfunctionRate <- 0.01;

	action provideDataAnalysisReport(float instabilityLevel) {
		print("Instability Level: " + instabilityLevel);
		ResourceManager.monitorLumifloraDensity();
	}

	action sendAlert(float instabilityLevel) {
		if (instabilityLevel > 0.8) {
			ResourceManager.alertShadowstalkersOfResourceScarcity();
		}
	}
}

species LumifloraPopulation {
	float growthRate <- 0.1;
	float stressLevel <- 1;
	float resourceAvailability <- 0.8;

	action updateGrowthRate(float rate) {
		growthRate <- rate;
	}

	action consumeResources(int amount) {
		resourceAvailability <- resourceAvailability - amount;
	}

	action sendResourceAvailabilitySignal() {
		Shadowstalker.forageForLumiflora();
	}
}
