            socialContactsPerDay <- max(1, socialContactsPerDay - 2);
        } else {
            socialContactsPerDay <- 5;
        }
    }

    reflex interact_with_peers {
        list peers <- one_of(communityMembers, socialContactsPerDay);
        foreach (peer in peers) {
            if (infected? and not peer.recovered?) {
                float risk <- transmission_probability * (1 - peer.complianceLevel);
                if (risk > rnd(1)) {
                    peer.infected? <- true;
                    peer.infectionDuration <- 0;
                }
            }
        }
    }

    reflex update_health_status {
        if (infected?) {
            infectionDuration <- infectionDuration + 1;
            if (infectionDuration > 14) {
                infected? <- false;
                recovered? <- true;
            }
        }
    }

    aspect base {
        draw circle(3) color: #2ca02c;
    }
}

species HealthcareFacility {
    string name;
    int bedCapacity <- 100;
    int patientLoad <- 0;
    float resourceUtilizationRate <- 0.0;
    float staffAvailability <- 1.0; // percentage
    float predicted_patient_load <- 0.0;
    float resource_allocation <- 0.0;
    float patient_outcome_metric <- 1.0;

    reflex anticipate_patient_demand {
        int infected_count <- count(communityMembers where: it.infected? == true);
        predicted_patient_load <- (count(communityMembers) > 0) ? (infected_count / count(communityMembers)) * bedCapacity : 0.0;
    }

    reflex report_patient_outcomes {
