    }

    reflex manage_resources {
        resourceUtilizationRate <- min(1.0, (bedCapacity > 0) ? patientLoad / bedCapacity : 0.0);
        staffAvailability <- max(0.5, staffAvailability - 0.1 * resourceUtilizationRate);
    }

    action update_operational_policies {
        if (resourceUtilizationRate > 0.8) {
            staffAvailability <- min(1.0, staffAvailability + 0.2);
        }
    }

    aspect base {
        draw rectangle(8) color: #d62728;
    }
}

