}

species containment_breach_log {
  int           breach_count      <- 0;
  list<string>  breach_locations;

  action recordBreach(string loc) {
    breach_count <- breach_count + 1;
    breach_locations::add(loc);
  }
}

species hexagonal_terrain_panel {
  int    panel_id;
  float  vegetation_density <- 0.5;
  float  regen_rate         <- 0.05;
  float  max_density        <- 1.0;

  reflex regenerateVegetation {
    vegetation_density <- min(vegetation_density + regen_rate, max_density);
  }

  aspect base {
    draw circle(0.5) color: #00ff00 opacity: vegetation_density;
  }
}

species light_control_module {
  int    module_id;
  float  intensity          <- 0.5;
  float  modulation_factor  <- 0.01;
  float  max_density        <- 1.0;  

  action setIntensity(float v) {
    intensity <- v;
  }

  reflex modulateLight {
    ask hexagonal_terrain_panel {
      vegetation_density <-
        min(vegetation_density + intensity * modulation_factor, max_density);
    }
  }

  aspect base {
    draw square(0.3) color: #ffff00 opacity: 0.8;
  }
}

species ecologist_analyst {
  int id;

  action configureSimulationParameters() {
    ask simulation_session {
      barrier_strength            <- barrierStrengthThreshold;
      light_gain_level            <- lightGainLevel;
      pesticide_intervention_flag <- pesticideIntervention;
    }
  }

  action viewPopulationDynamicsGraph() {
    write("Dynamics: t="  + population_series.timestamps
          + " | Prey="    + population_series.prey_counts
          + " | Predator="+ population_series.predator_counts);
  }
}

species engineer_operator {
  int id;

  reflex monitorEnvironmentalSensors {
    write("SensorLog: intensity="
          + light_control_module.intensity 
          + " | barrier=" 
          + simulation_session.barrier_strength);
  }

  action adjustLightGain(float gain) {
    ask light_control_module { setIntensity(gain); }
  }

  action calibrateControlAlgorithms() {
    float dev <- simulation_session.eq_deviation;
    ask light_control_module {
      intensity <- intensity + dev * 0.01;
    }
  }
}

species regulator_observer {
  int  id;
  bool complianceStatus;

  action reviewContainmentBreaches() {
    ask containment_breach_log {
      write("Breaches: " + breach_count + " at " + breach_locations);
    }
  }

  action validateBiosecurityCompliance() {
    complianceStatus <- 
      (containment_breach_log.breach_count == 0
       and simulation_session.barrier_strength >= barrierStrengthThreshold);
    write("Compliance: " + complianceStatus);
  }

  action generateComplianceReport() {
    write("Report: Session " + simulation_session.session_id
          + " Compliance=" + complianceStatus);
  }
}

species entrepreneur_interface {
  int id;

  action exportPerformanceData() {
    write("ExportSummary: " + simulation_session.translational_report);
  }

  action compareInterventionStrategies() {
    if (simulation_session.pesticide_intervention_flag) {
      write("InterventionApplied: reduces pest by 50%");
    } else {
      write("NoIntervention: deviation=" + simulation_session.eq_deviation);
    }
  }

  action publishPerformanceReport() {
    write("Publish: " + simulation_session.translational_report);
  }
}

experiment PredatorPreyExperiment type: gui {
  parameter barrierStrengthThreshold var:barrierStrengthThreshold min:0.5 max:1.0 step:0.05;
  parameter lightGainLevel         var:lightGainLevel         category: Low, Medium, High;
